// Bulk operations for performance
public async Task BulkInsertAsync<T>(IEnumerable<T> entities) where T : class
{
    const int batchSize = 1000;
    var entityList = entities.ToList();

    for (int i = 0; i < entityList.Count; i += batchSize)
    {
        var batch = entityList.Skip(i).Take(batchSize);
        await _context.Set<T>().AddRangeAsync(batch);
        await _context.SaveChangesAsync();
        _context.ChangeTracker.Clear(); // Prevent memory buildup
    }
}

