// Sharding strategy
public class ShardedDbContextFactory : IDbContextFactory<ShardedDbContext>
{
    private readonly IConfiguration _configuration;
    private readonly IShardResolver _shardResolver;

    public ShardedDbContext CreateDbContext(string shardKey)
    {
        var connectionString = _shardResolver.GetConnectionString(shardKey);
        var optionsBuilder = new DbContextOptionsBuilder<ShardedDbContext>();
        optionsBuilder.UseSqlServer(connectionString);
        return new ShardedDbContext(optionsBuilder.Options);
    }
}

// Registration
builder.Services.AddSingleton<IDbContextFactory<ShardedDbContext>, ShardedDbContextFactory>();

// Audit logging implementation
public class AuditableDbContext : DbContext
{
    public override async Task<int> SaveChangesAsync(CancellationToken cancellationToken = default)
    {
        var auditEntries = new List<AuditEntry>();

        foreach (var entry in ChangeTracker.Entries())
        {
            if (entry.Entity is IAuditable auditable && entry.State != EntityState.Unchanged)
            {
                auditable.ModifiedAt = DateTime.UtcNow;
                auditable.ModifiedBy = _currentUserService.UserId;

                auditEntries.Add(new AuditEntry
                {
                    EntityType = entry.Entity.GetType().Name,
                    Action = entry.State.ToString(),
                    Timestamp = DateTime.UtcNow,
                    Changes = GetChanges(entry)
                });
            }
        }

        var result = await base.SaveChangesAsync(cancellationToken);
        await LogAuditAsync(auditEntries);
        return result;
    }
}

// Distributed transaction handling
public class TransactionalService
{
    private readonly AppDbContext _context;
    private readonly IMessageBus _messageBus;

    public async Task ProcessWithSagaAsync(Order order)
    {
        using var transaction = await _context.Database.BeginTransactionAsync();
        try
        {
            // Update local database
            _context.Orders.Add(order);
            await _context.SaveChangesAsync();

            // Publish event for saga orchestration
            await _messageBus.PublishAsync(new OrderCreatedEvent(order.Id));

            await transaction.CommitAsync();
        }
        catch
        {
            await transaction.RollbackAsync();
            throw;
        }
    }
}

// Temporal table configuration
public class AppDbContext : DbContext
{
    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        modelBuilder.Entity<Product>(entity =>
        {
            entity.ToTable("Products", tb => tb.IsTemporal(ttb =>
            {
                ttb.HasPeriodStart("ValidFrom");
                ttb.HasPeriodEnd("ValidTo");
                ttb.UseHistoryTable("ProductHistory");
            }));
        });
    }
}

