// Global query filters
protected override void OnModelCreating(ModelBuilder modelBuilder)
{
    // Soft delete filter
    modelBuilder.Entity<BaseEntity>()
        .HasQueryFilter(e => !e.IsDeleted);

    // Multi-tenant filter
    modelBuilder.Entity<TenantEntity>()
        .HasQueryFilter(e => e.TenantId == _currentTenantService.TenantId);

    // Security filter
    modelBuilder.Entity<SecureEntity>()
        .HasQueryFilter(e => e.UserId == _currentUserService.UserId || _currentUserService.IsAdmin);
}

// Bypassing filters when needed
var allEntities = await context.MyEntities
    .IgnoreQueryFilters()
    .ToListAsync();

// Owned entity configuration
protected override void OnModelCreating(ModelBuilder modelBuilder)
{
    modelBuilder.Entity<Customer>()
        .OwnsOne(c => c.Address, address =>
        {
            address.Property(a => a.Street).HasColumnName("Street");
            address.Property(a => a.City).HasColumnName("City");
            address.Property(a => a.Country).HasColumnName("Country");
        });

    modelBuilder.Entity<Order>()
        .OwnsMany(o => o.LineItems, lineItem =>
        {
            lineItem.WithOwner().HasForeignKey("OrderId");
            lineItem.Property(li => li.ProductId);
            lineItem.Property(li => li.Quantity);
            lineItem.Property(li => li.Price).HasColumnType("decimal(18,2)");
        });
}

