// Compiled queries for performance
public static class CompiledQueries
{
    public static readonly Func<AppDbContext, int, Task<User>> GetUserById =
        EF.CompileAsyncQuery((AppDbContext context, int id) =>
            context.Users.FirstOrDefault(u => u.Id == id));

    public static readonly Func<AppDbContext, string, IAsyncEnumerable<Product>> GetProductsByCategory =
        EF.CompileAsyncQuery((AppDbContext context, string category) =>
            context.Products.Where(p => p.Category == category));

    public static readonly Func<AppDbContext, DateTime, DateTime, Task<int>> GetOrderCountInRange =
        EF.CompileAsyncQuery((AppDbContext context, DateTime start, DateTime end) =>
            context.Orders.Count(o => o.CreatedAt >= start && o.CreatedAt <= end));
}

// Usage
var user = await CompiledQueries.GetUserById(context, userId);
var products = CompiledQueries.GetProductsByCategory(context, "Electronics");

// Change tracking optimization
public class OptimizedDbContext : DbContext
{
    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
    {
        // Disable automatic change detection for bulk operations
        ChangeTracker.AutoDetectChangesEnabled = false;
        ChangeTracker.QueryTrackingBehavior = QueryTrackingBehavior.NoTracking;
    }

    public async Task BulkUpdateAsync<T>(IEnumerable<T> entities) where T : class
    {
        try
        {
            ChangeTracker.AutoDetectChangesEnabled = false;

            foreach (var entity in entities)
            {
                Entry(entity).State = EntityState.Modified;
            }

            await SaveChangesAsync();
        }
        finally
        {
            ChangeTracker.AutoDetectChangesEnabled = true;
        }
    }
}

